import React, { Component } from 'react';
import './App.css';
import HeatMap from './HeatMap';
import * as algorithms from './algorithms';
class App extends Component {
  constructor(props) {
  super(props);
  this.state = {
  mydata2:{
    '2005-2010_Graduation_Outcomes_-_School_Level.csv': 
    ['Demographic',
'Student Age',
'Cohort',
'Still Enrolled - % of cohort',
'Dropped Out - n',
'Dropped Out - % of cohort'],
'2006-2007_School_Progress_Report.csv': ['DBN',
'DISTRICT',
'PERCENT OF STUDENTS IN BOROUGH / GRADE / PROGRAM / SUBJECT'],
'2010-2011_Class_Size_-_School-level_detail.csv': ['CSD',
'CORE COURSE (MS CORE and 9-12 ONLY)',
'SERVICE CATEGORY(K-9* ONLY)',
'NUMBER OF STUDENTS / SEATS FILLED',
'NUMBER OF SECTIONS',
'AVERAGE CLASS SIZE',
'SIZE OF SMALLEST CLASS',
'SIZE OF LARGEST CLASS',
'DATA SOURCE',
'SCHOOLWIDE PUPIL-TEACHER RATIO'],
'2011_-_2016_Demographic_Snapshot.csv': ['DBN',
'Student Age',
'Year',
'Total Enrollment',
'Grade PK',
'Grade K',
'Grade',
'Present',
'Released'],
'Local_Law_14_Health_Data_-_HS_School.csv': ['School DBN',
'Community School District',
'City Council District',
'Student Age',
'SCHOOL CODE',
'NUMBER OF SECTIONS',
'AVERAGE CLASS SIZE',
'SIZE OF SMALLEST CLASS',
'SIZE OF LARGEST CLASS',
'DATA SOURCE',
'GRADE ',
'PROGRAM TYPE',
'CORE SUBJECT (MS CORE and 9-12 ONLY)',
'CORE COURSE (MS CORE and 9-12 ONLY)',
'SERVICE CATEGORY(K-9* ONLY)',
'NUMBER OF STUDENTS / SEATS FILLED',
'NUMBER OF SECTIONS','# of 15-16 June and August graduates',
'# of 15-16 June and August graduates meeting high school health requirements',
'%2']},
      unionmade:[],
      arr:[],
      clickedA:[],
      groups:['dataset2','dataset3'],
      matrixdata:{},
  }
 this.clickhandle=this.clickhandle.bind(this);
 this.clickhandle2=this.clickhandle2.bind(this);
 this.Unionmaker=this.Unionmaker.bind(this);
 this.attribute_click_handler=this.attribute_click_handler.bind(this);


};
//----------------------------------------------------Union Maker
Unionmaker(mydata){
  var mySet= new Set();
  for(var key in mydata){
      for(var i=0;i<mydata[key].length;i++){
          mySet.add(mydata[key][i]);
      }
      }
return Array.from(mySet);
}
//-----------------------------------------------------clickhandle starts here
clickhandle(ab){
//console.log(ab);
var arr=[];
var clickedAA=[...this.state.clickedA,ab];
this.setState({clickedA:clickedAA});
console.log(this.state.clickedA)
for(var i=0;i<1;i++){
  var a = <HeatMap display='main' clickhandler={this.attribute_click_handler} datasets={this.state.matrixdata} commonA={this.state.unionmade} />
  arr.push(a)  
}
this.setState({arr:arr});
//console.log(this.state.matrixdata);
}
//-----------------------------------------------------View2 click handler starts here
clickhandle2(){
}
//-----------------------------------------------------attribute_click_handler starts here
attribute_click_handler(ab){
  var arr=this.state.clickedA;
  arr.push(ab)
  this.setState({clickedA:arr},()=>{
    console.log(this.state.clickedA)
  })
  //console.log(ab)
  }


//-----------------------------------------------------ComponentDidmount
componentDidMount(){
  var unionedA=this.Unionmaker(this.state.mydata2);
  this.setState({unionmade:unionedA})
  //console.log(unionedA)
  var matrixdata=algorithms.matrixgen(unionedA,this.state.mydata2);
  this.setState({matrixdata:matrixdata})
}

render() {
    return (
     <div>
      <div>
      <button onClick={this.clickhandle}> View </button>
      <button onClick={this.clickhandle2}> View2 </button>
      </div>
      <div>{this.state.arr}</div>

    </div>
    );
  }
}
export default App;






