  this.setState({
    showPopup: !this.state.showPopup
  });
}
//---------------------------------------------------------------XYgenerator function starts here
xygenerator(s1c1,s2c1,lineId){
  var x1 = $(s1c1).offset().left + ($(s1c1).width()/2);
  var y1 = $(s1c1).offset().top + ($(s1c1).height());
  var x2 = $(s2c1).offset().left + ($(s2c1).width()/2);
  var y2 = $(s2c1).offset().top;
  this.drawline(lineId,x1,x2,y1,y2);
}
