  //console.log(unionedA)
  var matrixdata=algorithms.matrixgen(unionedA,this.state.mydata2);
  this.setState({matrixdata:matrixdata})
}

render() {
    return (
     <div>
      <div>
      <button onClick={this.clickhandle}> View </button>
      <button onClick={this.clickhandle2}> View2 </button>
      </div>
      <div>{this.state.arr}</div>
      <div>
        {this.state.groups}
      </div>
    </div>
    );
  }
}
export default App;






