import React,{Component} from 'react';
import * as d3 from 'd3';
class Piechart extends Component{
  handleClick(d) {
    console.log(d);
   }
render(){
const width = 960;
const height = 500;
const colour = d3.scaleOrdinal(["#98abc5", "#8a89a6", "#7b6888", "#6b486b", "#a05d56", "#d0743c", "#ff8c00"]);
return(
<div>
  <svg width={width} height={height}>
    <g transform={`translate(${width / 8}, ${height / 2})`}>

    {
        <text>Hello</text>
    }
    {
        this.props.dataset.map((d, i) => (<rect key={i} onClick={() => this.handleClick(d)} className="rec" fill={colour(d)} x={i*10} y={0} width='10' height="10" > </rect>))
    }
    {
        this.props.dataset.map((d, i) => (<rect key={i} onClick={() => this.handleClick(d)} className="rec" fill={colour(d)} x={i*10} y={12} width='10' height="10" > </rect>))
    }
    </g>
  </svg>
</div>
)
}}
export default Piechart;
