import React,{Component} from 'react';
import * as d3 from 'd3';
class Piechart extends Component{
  handleClick(d) {
    console.log(this.props.dataset);
   }
render(){
const width = 960;
const height = 100;
const colour = d3.scaleOrdinal(["#98abc5", "#8a89a6", "#7b6888", "#6b486b", "#a05d56", "#d0743c", "#ff8c00"]);
return(
<div>
<div className="Row">Hello </div>  
<svg width={width} height={height}>
    <g transform={`translate(${width / 8}, ${height / 2})`}>
    {
        this.props.dataset.map((d, i) => (<rect 
        className="Cell"
        key={i} 
        onClick={() => this.handleClick(d)} 
        className="rec" 
        fill={d==1? "red":"white"} 
        x={i*18} 
        y={0} 
        width='18' 
        height="18" 
        strokeWidth=".1" 
        stroke="black" > </rect>))
    }
    </g>
  </svg>
</div>
)
}}
export default Piechart;
