import React, { Component } from 'react'
import './App.css'
import * as d3 from 'd3'
class BarChart extends Component {
   constructor(props){
      super(props)
      this.createHeatMap = this.createHeatMap.bind(this)
   }
   componentDidMount() {
      this.createHeatMap()
   }
   componentDidUpdate() {
      this.createHeatMap()
   }
   //-------------------------------------------------------------------------------------CreateHeatMap starts here
   createHeatMap() {
    const node = this.node
    var itemSize = 22,
    cellSize = itemSize-.5,
    margin = {top: 120, right: 20, bottom: 20, left: 110};

    var width = 950 - margin.right - margin.left,
    height = 500 - margin.top - margin.bottom;
    /*
    var data=[
      ['Dataset1','Hello everyone! I am mamun',1],
      ['Dataset1','Good morning',0],
      ['Dataset1','Hello everyone! I am',1],
      ['Dataset1','Good morning Everyoe',1],
      ['Dataset2','Hello everyone! I am mamun',1],
      ['Dataset2','Good morning Everyoe',0],  
      ['Dataset2','Hello everyone! I am',1],
      ['Dataset2','Good morning',0],
      ['Dataset3','Hello everyone! I am mamun',1],
      ['Dataset3','Good morning Everyoe',0],  
      ['Dataset3','Hello everyone! I am',1],
      ['Dataset3','Good morning',0]
      ]
      */
     var data=this.props.datasets;
    //console.log(data);
    var x_elements = d3.set(data.map(function( d ) { return d[1]; } )).values();
    //console.log(x_elements);
    var y_elements = d3.set(data.map(function( d ) { return d[0]; } )).values();

    //------------------------------------------------------- Ordinal Scaling for X axis
    var xScale = d3.scale.ordinal()
      .domain(x_elements)
      .rangeBands([0, x_elements.length * itemSize]);
    var xAxis = d3.svg.axis()
      .scale(xScale)
      .tickFormat(function (d) {
          return d;
      })
      .orient("top");
    //------------------------------------------------------- Ordinal Scaling for Y axis
    var yScale = d3.scale.ordinal()
      .domain(y_elements)
      .rangeBands([0, y_elements.length * itemSize]);
    var yAxis = d3.svg.axis()
      .scale(yScale)
      .tickFormat(function (d) {
          return d;
      })
      .orient("left");

    var svg = d3.select(node)
      .append("svg")
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

    var cells = svg.selectAll('rect')
      .data(data)
      .enter().append('g').append('rect')
      .attr('class', 'cell')
      .attr('width', cellSize)
      .attr('height', cellSize)
      .attr('y', function(d) { return yScale(d[0]); })
      .attr('x', function(d) { return xScale(d[1]); })
      .attr('fill', (d)=> d[2]==1? '#fe9922':"grey")

    //------------------------------------------------------- Axis
    svg.append("g")
      .attr("class", "y axis")
      .call(yAxis)
      .selectAll('text')
      .on('click',(d,i)=>console.log(d))
      .attr('font-weight', 'normal');

    svg.append("g")
      .attr("class", "x axis")
      .call(xAxis)
      .selectAll('text')
      .attr('font-weight', 'normal')
      .style("text-anchor", "start")
      .attr("dx", ".8em")
      .attr("dy", ".5em")
      .on('click',(d,i)=>console.log(d,i))
      .attr("transform", function (d) {
          return "rotate(-45)";    
      });
         }
render() 
{
      return <svg ref={node => this.node = node}
      width={500} height={500}>
      </svg>
}
}
export default BarChart
