import React,{Component} from 'react';
class Row extends Component{
constructor(props) {
    super(props);
    this.state={items:[],rows:[]}
    this.rowgen=this.rowgen.bind(this);  
    }
rowgen(){
    var myrows=[];
    var temp;
    for(var i=0;i<this.props.idata.length;i++){
        for(var j=0;j<this.props.data.length;j++){
            if(this.props.idata[i]==this.props.data[j]){
                temp=<rect key={i} fill="white" x={i*18} y={0} width='18' height="18" strokeWidth=".1" stroke="black" > </rect>;
                myrows.push(temp);
            }
        }
    }
    this.setState({rows:myrows})
    console.log(this.state.rows)
    //this.props.data.map((d, i) => (<rect key={i} fill="white" x={i*18} y={0} width='18' height="18" strokeWidth=".1" stroke="black" > </rect>))
}
render(){
    const width = 960;
    const height = 16;
return(
<div>
    <div className="Table">
        <div className="Row">
            <div className="Cell">
            {this.props.dataset}
            </div>
            <div className="Cell">
            <button onClick={this.rowgen}>Click</button>
            <svg width={width} height={height}> 
            {
            this.state.rows
            }
            </svg>
            </div>
        </div>
        </div>        
</div>
)
}}
export default Row;
