import React, { Component } from 'react'
import './App.css'
import * as d3 from 'd3'
class BarChart extends Component {
   constructor(props){
      super(props)
      this.createHeatMap = this.createHeatMap.bind(this)
   }
   componentDidMount() {
      this.createHeatMap()
   }
   componentDidUpdate() {
      this.createHeatMap()
   }
   //-------------------------------------------------------------------------------------CreateHeatMap starts here
   createHeatMap() {
      const node = this.node
      var itemSize = 22,
      cellSize = itemSize-.5,
      margin = {top: 120, right: 20, bottom: 20, left: 110};

      var width = 750 - margin.right - margin.left,
      height = 300 - margin.top - margin.bottom;

      d3.csv('data2.csv', function ( response ) {
        var data = response.map(function( item ) {
            var newItem = {};
            newItem.row = item.x;
            newItem.column = item.y;
            newItem.value = item.value;
            return newItem;
        })

      var x_elements = d3.set(data.map(function( item ) { return item.column; } )).values();
      var y_elements = d3.set(data.map(function( item ) { return item.row; } )).values();

      //------------------------------------------------------- Tooltip
      const tip=d3.tip()
              .attr('class','tip card')
              .html(d=> {
                let content = `<div class="name">${d.row}</div>`;
                content += `<div class="cost">${d.column}</div>`;
                content += `<div class="cost">${d.value}</div>`;
                return content;
              })


      //------------------------------------------------------- Ordinal Scaling for X axis
      var xScale = d3.scale.ordinal()
        .domain(x_elements)
        .rangeBands([0, x_elements.length * itemSize]);

      var xAxis = d3.svg.axis()
        .scale(xScale)
        .tickFormat(function (d) {
            return d;
        })
        .orient("top");
      //------------------------------------------------------- Ordinal Scaling for Y axis
      var yScale = d3.scale.ordinal()
        .domain(y_elements)
        .rangeBands([0, y_elements.length * itemSize]);

      var yAxis = d3.svg.axis()
        .scale(yScale)
        .tickFormat(function (d) {
            return d;
        })
        .orient("left");
      //------------------------------------------------------- color scale
      var colorScale = d3.scale.linear()
        .domain([0.85, 1])
        .range(["#ef5350","#EC407A","#AB47BC","#7E57C2","#5C6BC0","#42A5F5","#26C6DA","#26A69A","#D4E157","#FFEE58","#FFA726"]);
      console.log(colorScale(0))
      var svg = d3.select(node)
        .append("svg")
        .attr("width", width + margin.left + margin.right)
        .attr("height", height + margin.top + margin.bottom)
        .append("g")
        .attr("transform", "translate(" + margin.left + "," + margin.top + ")");

      var cells = svg.selectAll('rect')
        .data(data)
        .enter().append('g').append('rect')
        .attr('class', 'cell')
        .attr('width', cellSize)
        .attr('height', cellSize)
        .attr('y', function(d) { return yScale(d.row); })
        .attr('x', function(d) { return xScale(d.column); })
        .attr('fill', (d)=> d.value==1? '#fe9922':"grey")
        .on("mouseover",function(){
          tooltip.style('display', null)
      })
      .on("mouseout",function(){
          tooltip.style("display","none");
      })
      .on("mousemove",function(d,i,n)
      {
          var xPos=d3.mouse(this)[0] - 15;
          var yPos=d3.mouse(this)[1] - 55;
          tooltip.attr('transform', 'translate(' + xPos + ',' + yPos + ')');
          var a=d['row']+", "+d['column']+", "+d['value'];
          tooltip.select("text").text(a);
      });
      //------------------------------------------------------- Axis
      svg.append("g")
        .attr("class", "y axis")
        .call(yAxis)
        .selectAll('text')
        .attr('font-weight', 'normal');
      svg.append("g")
        .attr("class", "x axis")
        .call(xAxis)
        .selectAll('text')
        .attr('font-weight', 'normal')
        .style("text-anchor", "start")
        .attr("dx", ".8em")
        .attr("dy", ".5em")
        .attr("transform", function (d) {
            return "rotate(-45)";
        });
      //---------------Tooltip

      var tooltip=svg.append("g").attr('class',tooltip).style('display','none');
      tooltip.append("text")
              .attr("x",15)
              .attr("dy","1.2em")
              .style("font-size","1.2em")
              .attr("font-weigth","bold");
      });
         }
render() 
{
      return <svg ref={node => this.node = node}
      width={500} height={500}>
      </svg>
}
}
export default BarChart
