import React,{Component} from 'react';
import * as d3 from 'd3';
class Piechart extends Component{
  handleClick(d) {
    console.log(d);
   }
render(){
const width = 960;
const height = 500;
//const colour = d3.scaleOrdinal(["#98abc5", "#8a89a6", "#7b6888", "#6b486b", "#a05d56", "#d0743c", "#ff8c00"]);
return(
<div>
  <form onSubmit={this.mySubmit}>
    <input type="text" onChange={this.mychangeHandler}></input>
  </form>
  <button onClick={this.handleClick}>Activate Lasers</button>
  <svg width={width} height={height}>
    <g transform={`translate(${width / 8}, ${height / 2})`}>
      {this.props.dataset.map((d, i) => ( <rect key={i} onClick={() => this.handleClick(d)} className="rec" x={i*40} y={height/2-d} width='35' height={d} > </rect>))}
    </g>
  </svg>
</div>
)
}}
export default Piechart;
