import React, { Component } from 'react';
import Piechart from './Piechart';
import Listtest from './listtest';
import Row from './row';
import './App.css';
class App extends Component {
  constructor(props) {
  super(props);
  this.state = {
    dataset1: ['A','B','C','D'],
    dataset2: ['AA','CC','BB','DD','EE'],
    dataset3: ['AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE'],
    cdata:['A','B','C','D','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD'],
    idata:['A','B'],
    myrect:[]
  }
  this.handleClick=this.handleClick.bind(this);
  this.rowgen=this.rowgen.bind(this);
};
//----------------------------------------------------Handle click starts here
handleClick(d){
  var data = this.state.idata;
  var mydata=[...data,d]
  this.setState({idata: mydata},()=>{
    console.log('Inside')
    this.rowgen(this.state.idata,"Dataset1",this.state.dataset1);  
  });

  console.log("clicked")
  //this.rowgen(this.state.idata,"Dataset1",this.state.dataset1);  
  /* for(var i=0;i<10;i++){
    this.rowgen(this.state.idata,"Dataset1",this.state.dataset1)  
  }
  */
}
//----------------------------------------------------Row generator starts here
rowgen(idata,datasetname,dataset){
  var a=[];
  a.push(<Row idata={idata} dataset={datasetname} data={dataset}/>)
  this.setState(prevState => ({
    myrect: [...prevState.myrect, a] 
  }));
}
//----------------------------------------------------Row generator ends here
  render() {
    const width = 960;
    const height = 26;
    //console.log(this.state.datasets)
    return (
      <div className="App">
        <div className="Cell">
        <svg width={width} height={height}>  
        {
          this.state.cdata.map((d, i) => (<text key={i} x={i*30} y={20} width='38' height="26" strokeWidth=".1" stroke="black" onClick={() => this.handleClick(d)}>{d}</text>))
        }
        </svg>
        <svg width={width} height={height}>  
        {
          this.state.idata.map((d, i) => (<text key={i} x={i*30} y={20} width='38' height="26" strokeWidth=".1" stroke="black" onClick={() => this.handleClick(d)}>{d}</text>))
        }
        </svg> 
        </div>
        {this.state.myrect}
      </div>
    );
  }
}
export default App;




