import React, { Component } from 'react';
import Row from './row';
import './App.css';
import Piechart from './Piechart';
class App extends Component {
  constructor(props) {
  super(props);
  this.state = {
    datasets: {
              dataset2: ['A','B','C','D','E'],
              dataset3: ['AA','BB','B','CC','DD','EE'],
              dataset4: ['A','BBB','DDD','B','C'],
              },
    allatt: ['A','B','C','D','E','BB','CC','DD','EE','BBB','DDD'],
    idata:[1,1,1,0,1,48],
    myrect:[]
  }
 this.clickhandle=this.clickhandle.bind(this); 
};
clickhandle(){
      console.log("Hello");
      var a=new Array(10);
      var b=this.state.idata.concat(a);
      this.setState({idata:b})
      }

render() {
    return (
      <div className="App">
      <button onClick={this.clickhandle}>Click Me</button>
       <Piechart dataset={this.state.idata}></Piechart>
      </div>
    );
  }
}
export default App;






