import React, { Component } from 'react';
import Piechart from './Piechart';
import Listtest from './listtest';
import Row from './row';
import './App.css';
class App extends Component {
  constructor(props) {
  super(props);
  this.state = {
    dataset1: ['BB','AA','C','D','E'],
    dataset2: ['A','CC','BB','DD','EE','A','BB','CC','DD','EE','A','BB','CC','DD','EE'],
    dataset3: ['AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE'],
    cdata:['A','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD'],
    idata:['A','BB','C'],
    myrect:[]
  }
  this.handleClick=this.handleClick.bind(this);
  this.rowgen=this.rowgen.bind(this);
};
handleClick(d){
this.setState({idata:['A']})
console.log(this.state.idata);
}
//----------------------------------------------------Row generator starts here
rowgen(idata,datasetname,dataset){
  return <Row idata={idata} dataset={datasetname} data={dataset}/>
}
//----------------------------------------------------Row generator ends here
  render() {
    const width = 960;
    const height = 26;
    //console.log(this.state.datasets)
    return (
      <div className="App">
        <div className="Cell">
        <svg width={width} height={height}>  
        {
          this.state.cdata.map((d, i) => (<text key={i} x={i*30} y={20} width='38' height="26" strokeWidth=".1" stroke="black" onClick={() => this.handleClick(d)}>{d}</text>))
        }
        </svg>
        <svg width={width} height={height}>  
        {
          this.state.idata.map((d, i) => (<text key={i} x={i*30} y={20} width='38' height="26" strokeWidth=".1" stroke="black" onClick={() => this.handleClick(d)}>{d}</text>))
        }
        </svg> 
        </div>
        {this.rowgen(this.state.idata,"Dataset1",this.state.dataset1)}
      </div>
    );
  }
}
export default App;




