import React, { Component } from 'react'
import './App.css'
import * as d3 from 'd3'
class BarChart extends Component {
   constructor(props){
      super(props)
      this.state={
        width:400,
        height:200,
        test:1,
      }
      this.createHeatMap = this.createHeatMap.bind(this)
      this.prep = this.prep.bind(this)
      this.prep2 = this.prep2.bind(this)
   }
   componentDidMount() {
      this.createHeatMap()
   }
   componentDidUpdate() {
      this.createHeatMap()
   }
  //--------------------------------Prepare for HeatMap here
   prep(mydata,commonA){
    var arr=[]
    for (var i=0;i<mydata.datasets.length;i++){
        for(var j=0;j<commonA.length;j++){
            var arr2=[];
            arr2.push(mydata.datasets[i]);
            arr2.push(commonA[j])
            arr2.push(mydata.matrix[i][j]);
            //console.log(mydata.datasets[i],mydata.matrix[i][j],commonA[j]);
            arr.push(arr2);
        }

    }
    return arr;
}
prep2(mydata,commonA,gdatasets){
  var arr=[]
  for (var i=0;i<mydata.datasets.length;i++){
      for(var ai=0;ai<gdatasets.length;ai++){
          if(mydata.datasets[i]==gdatasets[ai]){
              for(var j=0;j<commonA.length;j++){
                  var arr2=[];
                  arr2.push(mydata.datasets[i]);
                  arr2.push(commonA[j])
                  arr2.push(mydata.matrix[i][j]);
                  //console.log(mydata.datasets[i],mydata.matrix[i][j],commonA[j]);
                  arr.push(arr2);
              }
          }    
      }
  }
  return arr;
}
   //-------------------------------------------------------------------------------------CreateHeatMap starts here
   createHeatMap() {
    const node = this.node
    var margin = {top: 320, right: 20, bottom: 20, left: 310};
    var width = this.state.width - margin.right - margin.left,
    height = this.state.height - margin.top - margin.bottom;
    if(this.props.display=='main'){
      var data=this.prep(this.props.datasets,this.props.commonA)
    }
    else{
      var data=this.prep2(this.props.datasets,this.props.commonA,this.props.gdatasets)
    }
    //console.log(data);
    var x_elements = d3.set(data.map(function( d ) { return d[1]; } )).values();
    //console.log(x_elements);
    var y_elements = d3.set(data.map(function( d ) { return d[0]; } )).values();

//------------------------------------------------------- Ordinal Scaling for X axis
    var xScale = d3.scale.ordinal()
      .domain(x_elements)
      .rangeBands([0, width]);
    var xAxis = d3.svg.axis()
      .scale(xScale)
      .tickFormat(function (d) {
          return d;
      })
      .orient("top");
//------------------------------------------------------- Ordinal Scaling for Y axis
    var yScale = d3.scale.ordinal()
      .domain(y_elements)
      .rangeBands([0, height]);
    var yAxis = d3.svg.axis()
      .scale(yScale)
      .tickFormat(function (d) {
          return d;
      })
      .orient("left");
//------------------------------------------------------- d3 Select starts here
    var svg = d3.select(node).append("svg").attr('border','2')
      .attr("width", width + margin.left + margin.right)
      .attr("height", height + margin.top + margin.bottom)
      .append("g")
      .attr("transform", "translate(" + margin.left + "," + margin.top + ")");
      svg.selectAll('rect')
      .data(data)
      .enter().append('g').append('rect')

      d3.select(node)
      .selectAll('rect')
      .data(data)
      .exit()
      .remove()

      d3.select(node)
      .selectAll('rect')
      .data(data)
      .attr('class', 'cell')
      .attr('width', width/this.props.commonA.length-1)
      .attr('height', height/this.props.datasets.datasets.length-1)
      .attr('y', function(d) { return yScale(d[0]); })
      .attr('x', function(d) { return xScale(d[1]); })
      .attr('fill', (d)=> d[2]==1? '#c994c7':"grey")


    //------------------------------------------------------- Axis
//----Y axis
    svg.append("g")
      .attr("class", "y axis")
      .call(yAxis)
      .selectAll('text')
      .on('click',(d,i)=>console.log(d))
      .attr('font-weight', 'normal');
//----X axis
    svg.append("g")
      .attr("class", "x axis")
      .call(xAxis)
      .selectAll('text')
      .attr('font-weight', 'normal')
      .style("text-anchor", "start")
      .attr("dx", ".8em")
      .attr("dy", ".5em")
      .on('click',(d,i)=> this.props.clickhandler(d))
      .attr("transform", function (d) {
          return "rotate(-45)";    
      });
//------------------------------------------------------- Border starts here
/*      
svg.append("rect")
      .attr("x", 0)
      .attr("y", 0)
      .attr("height", height)
      .attr("width", width)
      .style("stroke", 'blue')
      .style("fill", "none")
      .style("stroke-width", 3);        
*/
}
//------------CreateMap ends here
componentWillMount(){
  this.setState({height:this.props.datasets.datasets.length*100})
}
componentDidMount() {
  this.createHeatMap()
}
componentDidUpdate() {
  this.createHeatMap()
}
render() 
{
  console.log(this.state.height)
      return( 
      <svg ref={node => this.node = node}
      width={this.state.width} height={this.state.height}>
      </svg>
    )
}
}
export default BarChart
