import React, { Component } from 'react';
import Piechart from './Piechart';
import Listtest from './listtest';
import Row from './row';
import './App.css';
class App extends Component {
  constructor(props) {
  super(props);
  this.state = {
    datasets:{dataset1: ['BB','C','D','E'],
    dataset2: ['A','CC','BB','DD','EE','A','BB','CC','DD','EE','A','BB','CC','DD','EE'],
    dataset3: ['AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE'],
    cdata:['A','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD'],
    idata:['A','BB','C']
    },
    dataset1: ['BB','C','D','E'],
    dataset2: ['A','CC','BB','DD','EE','A','BB','CC','DD','EE','A','BB','CC','DD','EE'],
    dataset3: ['AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE','AAA','BBBB','C','DD','EE'],
    cdata:['A','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD','A','EE','CC','DD'],
    idata:['A','BB','C'],
    myrect:[]
  }
  this.handleClick=this.handleClick.bind(this);
};
handleClick(d){
this.setState({idata:['A']})
console.log(d);
}
//----------------------------------------------------Row generator starts here
rowgen(){
  var myrows=[];
  var temp;
  for(var j=0;j<this.props.data.length;j++){
      var flag=false;
      //console.log(this.props.data[j])
      for(var i=0;i<this.props.idata.length;i++){
          if(this.props.data[j]==this.props.idata[i]){ flag=true; } 
      }
      if(flag==true){
          temp=<rect key={j} fill="red" x={j*18} y={0} width='18' height="18" strokeWidth=".1" stroke="black" > </rect>;
      }
      else{
          temp=<rect key={j} fill="white" x={j*18} y={0} width='18' height="18" strokeWidth=".1" stroke="black" > </rect>;
      }
      myrows.push(temp);
  }
  this.setState({rows:myrows})
}
//----------------------------------------------------Row generator ends here
  render() {
    const width = 960;
    const height = 26;
    console.log(this.state.datasets)
    return (
      <div className="App">
        <div className="Cell">
        <svg width={width} height={height}>  
        {
          this.state.cdata.map((d, i) => (<text key={i} x={i*30} y={20} width='38' height="26" strokeWidth=".1" stroke="black" onClick={() => this.handleClick(d)}>{d}</text>))
        }
        </svg>
        <svg width={width} height={height}>  
        {
          this.state.idata.map((d, i) => (<text key={i} x={i*30} y={20} width='38' height="26" strokeWidth=".1" stroke="black" onClick={() => this.handleClick(d)}>{d}</text>))
        }
        </svg> 
        </div>
        <Row idata={this.state.idata} dataset="Dataset1" data={this.state.dataset1}/>
        <Row idata={this.state.idata} dataset="Dataset2" data={this.state.dataset2}/>
        <Row idata={this.state.idata} dataset="Dataset1" data={this.state.dataset3}/>
      </div>
    );
  }
}
export default App;




